import "./App.css";
import React, { useState, useEffect } from "react";
import axios from "axios";
import Summary from "./components/Summary";
import Nav from "./components/Nav";
import DayOneLive from "./components/DayOneLive";
import { UserData } from "./components/Data";
import BarChart from "./components/BarChart";
import AllCountries from "./components/AllCountries";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import News from "./components/News";
import {
  CountryContext,
  CurrentCountryContext,
} from "./components/CurrentCountryContext";

function App() {
  const [currentCountry, setCurrentCountry] = useState("");
  const [userData, setUserData] = useState({
    labels: UserData.map((data) => data.year),
    datasets: [
      {
        label: "User Gained",
        data: UserData.map((data) => data.userGain),
      },
    ],
  });

  useEffect(() => {
    async function getCountry() {
      const res = axios.get("https://ipapi.co/json/");
      console.log(res.data.country_name);
      setCurrentCountry(res.data.country_name);
    }
    getCountry();
  }, []);
  return (
    <CurrentCountryContext.Provider
      value={{ currentCountry, setCurrentCountry }}
    >
      <Router className="App">
        <Routes>
          <Route
            path="/"
            element={
              <div>
                <Nav />
                <Summary />
                <DayOneLive chartData={userData} />
              </div>
            }
          />
          <Route
            path="/countries"
            element={
              <div>
                <Nav />
                <AllCountries />
              </div>
            }
          />
          <Route
            path="/news"
            element={
              <div>
                <Nav />
                <News />
              </div>
            }
          />
        </Routes>
      </Router>
    </CurrentCountryContext.Provider>
  );
}
export default App;

