  const data = {
    labels: UserData.map((data) => data.year),
    dataset: [
      {
        data: UserData.map((data) => data.userGain),
      },
    ],
  };
  const options = {
    scales: {
      x: {
        grid: {
          offset: true,
        },
      },
    },
  };
  return (
    <div>
      <Bar data={data} options={options} />
    </div>
  );
}

export default DayOneLive;
