import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Summary.css";

function Summary() {
  const [summary, setSummary] = useState([]);
  const [date, setDate] = useState("");

  useEffect(() => {
    async function fetchSummary() {
      const request = await axios.get("https://api.covid19api.com/summary");
      console.log(request.data);
      setSummary(request.data.Global);
      const getDate = request.data.Date.split("T");
      setDate(getDate[0]);
    }
    fetchSummary();
    return () => {};
  }, []);

  console.log(summary);

  return (
    <div>
      <div className="date">
        <h4>Date</h4>
        <h4>{date}</h4>
      </div>
      <div className="summary">
        <div className="keys">
          <h3>New Confirmed</h3>
          <h5>{summary.NewConfirmed}</h5>
        </div>
        <div className="keys">
          <h3>New Deaths</h3>
          <h4>{summary.NewDeaths}</h4>
        </div>
        <div className="keys">
          <h3>New Recovered</h3>
          <h4>{summary.NewRecovered}</h4>
        </div>
        <div className="keys">
          <h3>Total Confirmed</h3>
          <h4>{summary.TotalConfirmed}</h4>
        </div>
        <div className="keys">
          <h3>Total Deaths</h3>
          <h4>{summary.TotalDeaths}</h4>
        </div>
        <div className="keys">
          <h3>Total Recovered</h3>
          <h4>{summary.TotalRecovered}</h4>
        </div>
      </div>
    </div>
  );
}

export default Summary;
