import React, { useEffect, useState } from "react";
import axios from "axios";
import "./AllCountries.css";

function AllCountries() {
  const [data, setData] = useState("");
  useEffect(() => {
    async function fetchData() {
      const response = await axios.get("https://api.covid19api.com/summary");
      console.log(response.data);
      setData(response.data.Countries);
    }
    fetchData();
  }, []);

  let dataSorted = [];
  let totalConfirmed = 0;
  let maxTotalConfirmed = "";
  //   while (dataSorted.length < data.length) {
  //     for (let i = 0; i < data.length; i++) {
  //       console.log(data[i].TotalConfirmed);
  //       if (i === 0) {
  //         totalConfirmed = data[i].TotalConfirmed;
  //       } else if (data[i].TotalConfirmed >= totalConfirmed) {
  //         totalConfirmed = data[i].TotalConfirmed;
  //         maxTotalConfirmed = data[i];
  //       }
  //     }
  //   }

  let arr = data.sort((a, b) => b.TotalConfirmed - a.TotalConfirmed);
  console.log(arr);
  console.log(totalConfirmed);
  console.log(maxTotalConfirmed);
  //console.log(Math.max(...dataSorted));
  return (
    <div className="countries__container">
      {Object.values(data).map((country) => (
        <div key={country.ID}>
          <h3>{country.Country}</h3>
          <h3>{country.TotalConfirmed}</h3>
          <h3>{country.NewConfirmed}</h3>
          <h3>{country.ToalDeaths}</h3>
          <h3>{country.NewDeaths}</h3>
          <h3>{country.TotalRecovered}</h3>
          <h3>{country.NewRecovered}</h3>
        </div>
      ))}
    </div>
  );
}

export default AllCountries;
