import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Summary.css";

function Summary() {
  const [summary, setSummary] = useState([]);
  const [date, setDate] = useState("");

  useEffect(() => {
    async function fetchSummary() {
      const req = await axios.get("https://ipapi.co/json/");
      console.log(req.data.country_name);
      const request = await axios.get("https://api.covid19api.com/summary");
      console.log(request.data);
      console.log(request.data.Countries);
      Object.values(request.data.Countries).map((country) =>
        // if(country.Country === "Germany"){
        //   console.log(country)
        // }
        console.log(country.Country)
      );
      // for (let i = 0; i <= request.data.Countries.length; i++) {
      //   if (request.data.Countries[i].Country === "Germany") {
      //     console.log(request.data.Countries[i]);
      //   }
      // }
      // console.log("Country:::::"+request.data)
      setSummary(request.data.Global);
      const getDate = request.data.Date.split("T");
      setDate(getDate[0]);
    }
    fetchSummary();
    return () => {};
  }, []);

  console.log(summary);

  return (
    <div>
      <div className="date">
        <h4>Date</h4>
        <h4>{date}</h4>
      </div>
      <div className="summary">
        <div className="keys">
          <h3>New Confirmed</h3>
          <h2 style={{ color: "red" }}>{summary.NewConfirmed}</h2>
        </div>
        <div className="keys">
          <h3>New Deaths</h3>
          <h2 style={{ color: "gray" }}>{summary.NewDeaths}</h2>
        </div>
        <div className="keys">
          <h3>New Recovered</h3>
          <h2 style={{ color: "green" }}>{summary.NewRecovered}</h2>
        </div>
        <div className="keys">
          <h3>Total Confirmed</h3>
          <h2 style={{ color: "darkslategray" }}>{summary.TotalConfirmed}</h2>
        </div>
        <div className="keys">
          <h3>Total Deaths</h3>
          <h2 style={{ color: "gray" }}>{summary.TotalDeaths}</h2>
        </div>
        <div className="keys">
          <h3>Total Recovered</h3>
          <h2 style={{ color: "green" }}>{summary.TotalRecovered}</h2>
        </div>
      </div>
    </div>
  );
}

export default Summary;
