import axios from "axios";
import React, { useEffect, useState } from "react";

function DayOneLive() {
  const [country, getCountry] = useState("");
  useEffect(() => {
    async function getCountry() {
      const request = await fetch("https://api.ipregistry.co/?key=tryout")
        .then(function (response) {
          return response.json();
        })
        .then(function (payload) {
          getCountry(payload.location.country.name);
          console.log(
            payload.location.country.name + ", " + payload.location.city
          );
        });
    }

    async function fetchSummary() {
      const request = await axios.get(
        `https://api.covid19api.com/dayone/country/${country}/status/confirmed`
      );
      console.log(request.data);
      //setSummary(request.data.Global);
      //const getDate = request.data.Date.split("T");
      //setDate(getDate[0]);
    }
    getCountry();
    fetchSummary();
    return () => {};
  }, []);
  return <div></div>;
}

export default DayOneLive;
