import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Summary.css";

function Summary() {
  const [summary, setSummary] = useState([]);

  let date = [];
  useEffect(() => {
    async function fetchSummary() {
      const request = await axios.get("https://api.covid19api.com/summary");
      console.log(request.data);
      setSummary(request.data.Global);
      date = request.data.Date.split("T");
      date = date[0];
      console.log(date);
    }
    fetchSummary();
    return () => {};
  }, []);

  console.log(summary);

  return (
    <div>
      <div className="date">
        <h3>Date</h3>
        <h3>{summary.Date.split("T")}</h3>
      </div>
      <div className="summary">
        <div className="keys">
          <h2>New Confirmed</h2>
          <h2>{summary.NewConfirmed}</h2>
        </div>
        <div className="keys">
          <h2>New Deaths</h2>
          <h2>{summary.NewDeaths}</h2>
        </div>
        <div className="keys">
          <h2>New Recovered</h2>
          <h2>{summary.NewRecovered}</h2>
        </div>
        <div className="keys">
          <h2>Total Confirmed</h2>
          <h2>{summary.TotalConfirmed}</h2>
        </div>
        <div className="keys">
          <h2>Total Deaths</h2>
          <h2>{summary.TotalDeaths}</h2>
        </div>
        <div className="keys">
          <h2>Total Recovered</h2>
          <h2>{summary.TotalRecovered}</h2>
        </div>
      </div>
    </div>
  );
}

export default Summary;
